Qr::Qr(string msg) : msg(msg)
{
    for (int i = 0; i < QR_SIZE; i++)
        for (int j = 0; j < QR_SIZE; j++)
            qr[i][j] = 0;

    get_data_stream(msg);
    polynomial_divide();
    assign_constant_pattern();
    fill_variable_slots();
    assign_mask();
};

