void Qr::get_data_stream(string inp)
{
    data_stream.push_back(0);
    data_stream.push_back(1);
    data_stream.push_back(0);
    data_stream.push_back(0);

    string temp = bitset<8>(inp.length()).to_string();
    for (char i : temp)
        data_stream.push_back(i - '0');

    for (char i : inp)
    {
        string t = bitset<8>(i).to_string();
        for (char j : t)
            data_stream.push_back(j - '0');
    }

    data_stream.push_back(0);
    data_stream.push_back(0);
    data_stream.push_back(0);
    data_stream.push_back(0);

    bool flag = false;
    for (int i = data_stream.size(); i < DATA_BITS; i += 8)
    {
        if (!flag)
        {
            data_stream.push_back(1);
            data_stream.push_back(1);
            data_stream.push_back(1);
            data_stream.push_back(0);
            data_stream.push_back(1);
            data_stream.push_back(1);
            data_stream.push_back(0);
            data_stream.push_back(0);
        }
        else
        {
            data_stream.push_back(0);
            data_stream.push_back(0);
            data_stream.push_back(0);
            data_stream.push_back(1);
            data_stream.push_back(0);
            data_stream.push_back(0);
            data_stream.push_back(0);
            data_stream.push_back(1);
        }
        flag = !flag;
    }
}

int Qr::gf_mul(int a, int b)
{
    int result = 0;
    while (b)
    {
        if (b & 1)
            result ^= a;
        a <<= 1;
        if (a & 0x100)
            a ^= 0x11D;
        b >>= 1;
    }
    return result;
}

void Qr::polynomial_divide()
{
    vector<int> message_bytes;
    for (int i = 0; i < data_stream.size(); i += 8)
    {
        int byte = 0;
        for (int j = 0; j < 8; j++)
        {
            byte = (byte << 1) | data_stream[i + j];
        }
        message_bytes.push_back(byte);
    }

    vector<int> remainder = message_bytes;
    while (remainder.size() < message_bytes.size() + EC_BITS / 8)
    {
        remainder.push_back(0);
    }

    int divisorLen = EC_BITS / 8 + 1;
    for (int i = 0; i < message_bytes.size(); i++)
    {
        int coef = remainder[i];
        if (coef != 0)
        {
            for (int j = 0; j < divisorLen; j++)
            {
                remainder[i + j] ^= gf_mul(divisor[j], coef);
            }
        }
    }

    for (int i = message_bytes.size(); i < remainder.size(); i++)
    {
        string bits = bitset<8>(remainder[i]).to_string();
        for (int j = 0; j < 8; j++)
        {
            ec_stream.push_back(bits[j] - '0');
        }
    }
}

void Qr::assign_constant_pattern()
{
    for (auto c : constant_pattern)
    {
        qr[c[0]][c[1]] = 1;
    }
}

void Qr::fill_variable_slots()
{
    for (int i = 0; i < DATA_BITS; i++)
    {
        qr[variable_slots[i][0]][variable_slots[i][1]] = data_stream[i];
    }
    for (int i = 0; i < EC_BITS; i++)
    {
        qr[variable_slots[i + DATA_BITS][0]][variable_slots[i + DATA_BITS][1]] = ec_stream[i];
    }
}

void Qr::assign_mask()
{
    for (auto t : mask)
    {
        qr[t[0]][t[1]] = !qr[t[0]][t[1]];
    }
}

void Qr::print_qr()
{
    int total_size = QR_SIZE + (2 * QUIET_ZONE);
    int expanded_qr[total_size][total_size];

    for (int i = 0; i < total_size; i++)
        for (int j = 0; j < total_size; j++)
            expanded_qr[i][j] = 0;

    for (int i = 0; i < QR_SIZE; i++)
        for (int j = 0; j < QR_SIZE; j++)
            expanded_qr[i + QUIET_ZONE][j + QUIET_ZONE] = qr[i][j];

    for (int i = 0; i < total_size; i += 2)
    {
        for (int j = 0; j < total_size; j++)
        {
            int top = expanded_qr[i][j];
            int bottom = 0;

            if (i + 1 < total_size)
                bottom = expanded_qr[i + 1][j];

            if (top == 1 && bottom == 1)
                cout << " ";
            else if (top == 1 && bottom == 0)
                cout << "▄";
            else if (top == 0 && bottom == 1)
                cout << "▀";
            else
                cout << "█";
        }
        cout << endl;
    }
}

void Qr::generate_svg(string output)
{
    if (output.size() < 4 || !(output.substr(output.size() - 4) == ".svg" || output.substr(output.size() - 4) == ".SVG"))
        output += ".svg";

    const int size = 29;
    const int module_size = 10;
    const int margin = 4 * module_size;
    const int total_size = (size * module_size) + (2 * margin);

    ofstream file(output);
    if (!file.is_open())
    {
        cout << "Error: Could not create file " << output << endl;
        return;
    }

    file << "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n";
    file << "<svg xmlns=\"http://www.w3.org/2000/svg\" ";
    file << "width=\"" << total_size << "\" height=\"" << total_size << "\" ";
    file << "viewBox=\"0 0 " << total_size << " " << total_size << "\" ";
    file << "shape-rendering=\"crispEdges\">\n";

    file << "<rect width=\"" << total_size
         << "\" height=\"" << total_size
         << "\" fill=\"white\"/>\n";

    file << "<path d=\"";
    for (int i = 0; i < size; i++)
    {
        for (int j = 0; j < size; j++)
        {
            if (qr[i][j] == 1)
            {
                int x = margin + (j * module_size);
                int y = margin + (i * module_size);

                file << "M" << x << " " << y
                     << "h" << module_size
                     << "v" << module_size
                     << "h-" << module_size
                     << "z ";
            }
        }
    }
    file << "\" fill=\"black\"/>\n";

    file << "</svg>\n";

    file.close();
    cout << "QR code SVG generated successfully: " << output << endl;
}